schemaVersions.content:
{
  "type": "record",
  "name": "StockReservation",
  "namespace": "com.example.retail",
  "fields": [
    {
      "name": "reservationId",
      "type": "int",
      "doc": "Unique identifier for the stock reservation"
    },
    {
      "name": "orderId",
      "type": "string",
      "doc": "Identifier for the order associated with the stock reservation"
    },
    {
      "name": "customerId",
      "type": "string",
      "doc": "Identifier for the customerid on the order"
    },
    {
      "name": "productId",
      "type": "string",
      "doc": "Identifier for the product being reserved"
    },
    {
      "name": "quantity",
      "type": "int",
      "doc": "Quantity of the product being reserved"
    },
    {
      "name": "reservationTime",
      "type": "string",
      "doc": "Timestamp indicating when the reservation was made"
    },
    {
      "name": "expiryTime",
      "type": "string",
      "doc": "Timestamp indicating when the reservation expires and the stock is released"
    }
  ],
  "doc": "Schema representing a stock reservation for an order in Acme Retail e-commerce application"
}
